import { useState,useEffect } from "react";

function Items(props) {
    const prod = props.produit;
    const image= prod.img[0];
    const [stock, setStock] = useState(9);
    const addToCard = () => {
        if (stock>1)
        {
            setStock(stock-1);
        } 
        else 
        {
            setStock("indisponible");
        }
    };

        return (

        <div className="product">
                <p className="prod">Produit {prod.id}</p>
                <img src={image} className={` 'large' : 'small'}`} alt="product" />
                <img src={prod.img[1]} className="petit" alt="product" />
                <img src={prod.img[2]} className="petit" alt="product" />
                {/* <img src={prod.img[3]} className="petit" alt="product" /> */}
                <p>Price :{prod.prix} Ar</p>
                <p>Stock : {stock}</p>
                <button className="btn" onClick={addToCard}>Add to card</button>
        </div>

            );

  }
export default Items;
